import React, { Component } from 'react';
import gift from './gift.png';
import './App.css';
import {Animated} from "react-animated-css";



class App extends Component {

  constructor() {
    super();
    this.handleClick = this.handleClick.bind(this);
    this.handleClickLikeOff = this.handleClickLikeOff.bind(this);
    this.handleClickLikeOn = this.handleClickLikeOn.bind(this);
    this.toggle = this.toggle.bind(this);
    this.state = {
      cadeauOpen: false,
    };
  }

  handleClickLikeOn() {
    this.setState({cadeauOpen: true});
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <Animated animationIn="bounceInLeft" animationOut="fadeOut" isVisible={true}>
            <div>
              <img onClick={this.handleClickLikeOn} src={gift} className="App-logo" alt="logo" />
            </div>
          </Animated>
          <Animated animationIn="tada" isVisible={true}>
            <h1 className="text">Les cadeaux de Moumousse</h1>
          </Animated>
        </header>
      </div>
    );
  }
}

export default App;
